app/src/main/java/com/example/mutemate/utils/AccessibilityUtils.kt
```kotlin:app/src/main/java/com/example/mutemate/utils/AccessibilityUtils.kt
package com.example.mutemate.utils

import android.accessibilityservice.AccessibilityServiceInfo
import android.content.Context
import android.content.Intent
import android.provider.Settings
import android.view.accessibility.AccessibilityManager

object AccessibilityUtils {

 /**
 * Check if our accessibility service is enabled
 */
 fun isAccessibilityServiceEnabled(context: Context): Boolean {
 val accessibilityManager = context.getSystemService(Context.ACCESSIBILITY_SERVICE) as AccessibilityManager
 val enabledServices = accessibilityManager.getEnabledAccessibilityServiceList(AccessibilityServiceInfo.FEEDBACK_ALL_MASK)

 for (service in enabledServices) {
 if (service.resolveInfo.serviceInfo.packageName == context.packageName) {
 return true
 }
 }
 return false
 }

 /**
 * Create an intent to open the accessibility settings
 */
 fun getAccessibilitySettingsIntent(): Intent {
 return Intent(Settings.ACTION_ACCESSIBILITY_SETTINGS)
 }
}
